const myCarouselElement = document.querySelector('#carouselExampleCaptions')
const carousel = new bootstrap.Carousel(myCarouselElement, {
    interval: 2000,
    touch: false
  })

document.addEventListener('DOMContentLoaded', function () {
    // Obtém os elementos dos botões
    var prevButton = document.querySelector('.carousel-control-prev');
    var nextButton = document.querySelector('.carousel-control-next');


    // Adiciona event listeners para os cliques nos botões
    prevButton.addEventListener('click', function () {
        slide(-1);
    });

    nextButton.addEventListener('click', function () {
        slide(1);
    });


});

// Função para manipular os slides (substitua conforme necessário)
function slide(direction) {
    var carousel = new bootstrap.Carousel(document.getElementById('carouselExampleCaptions'), {
        interval: false // Impede a animação automática do carrossel
    });

    if (direction === -1) {
        carousel.prev();
    } else {
        carousel.next();
    }
}